import React from 'react';
import {useState} from 'react';
import { StyleSheet,ImageBackground, View, TouchableOpacity ,Text} from 'react-native';

import Username from '../components/profile/Username'
import Time from '../components/profile/Time';
import Description from '../components/profile/Description';
import Posts from '../components/profile/Posts';
import Followers from '../components/profile/Followers';
import PostButton from '../components/profile/PostButton';
import Photos from '../screens/Photos';

import AntDesign from 'react-native-vector-icons/AntDesign';
import Entypo from 'react-native-vector-icons/Entypo';

import Modal from 'react-native-modal'



function Profile(props) {


    const [isModalVisible, setModalVisible] = useState(false);

    const toggleModal = () => {
        setModalVisible(!isModalVisible);
    };

    return (

        <View style={styles.container}>

            <ImageBackground
            style={styles.image}
            source={require('../assets/photo-1520262494112-9fe481d36ec3.jpeg')}>

            <Username/>
            <Time/>

            </ImageBackground>


            <Description></Description>

            <Posts></Posts>

            <Followers></Followers>

            <PostButton></PostButton>



            <AntDesign name="down" 
                    size={30} 
                    style={styles.downArrow} 
                    onPress={()=>console.log("tapped")}>
            </AntDesign>

            <Entypo name="documents" 
                    size={30} 
                    style={styles.options}
                    onPress={()=>console.log("tapped")}></Entypo>


            <TouchableOpacity style={styles.photosButtonI} onPress={toggleModal}><Text style={styles.photosButtonII}>Photos</Text></TouchableOpacity>

            <Modal  isVisible={isModalVisible}
                    onSwipeComplete={()=>{setModalVisible(false)}}
                    onBackdropPress={()=>{setModalVisible(false)}}
                    swipeDirection="down"

                    animationIn='pulse'
                    animationInTiming={500}

                    animationOut='fadeOut'
                    animationOutTiming={500}

                    style={styles.modalStyle}
                    >


                <Photos></Photos>

            </Modal>



        </View>

    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#e8e8e8',
        alignItems: 'center',
        justifyContent:'center'

    },


    image:{
        width:270, 
        height:400,


        alignItems:'center',
        justifyContent:'flex-end',


        overflow: 'hidden',
        borderRadius:20,
        position:'absolute',
        bottom:'35%',


    },

    downArrow:{
        position:'absolute',
        top:'7%',
        left:'5%'

    },

    options:{
        position:'absolute',
        top:'7%',
        right:'5%'

    },

    modalStyle:{
        flex: 1,
        justifyContent:'center',
        alignItems:'center',
        marginTop: 22
    },

    photosButtonI:{
        position:'absolute',
        bottom:'10%',
        borderWidth: 1.5,
        borderRadius: 4,
        padding: 0.5,
        backgroundColor:'#e8e8e8',
        borderColor:'white',  
    },

    photosButtonII:{
        borderWidth: 1.3,
        borderRadius: 4,
        padding: 4, 
        borderColor:'#991414',
        textAlign:'center',
        fontSize:16,
    }

});


export default Profile;
